INSERT INTO `pep_app_emailtemplate` VALUES (1,'Announcement','<p style=\"text-align:center\"><span style=\"font-size:36px\"><span style=\"font-family:Times New Roman,Times,serif\"><u><strong>Announncement</strong></u></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Dear [name],</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">We are excited to invite you to our upcoming event! Please mark your calendar for this exclusive announcement. As a valued member of our community, your participation is important to [name]us. Further details have been sent to your registered email [email] and linked to your account [account]. The event is scheduled for [date] [time]. We look forward to seeing you there and celebrating together! </span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Best regards, <br />\r\n<a href=\"https://www.brandexperts.ae/product/134\">Fresh fruits</a><br />\r\n<br />\r\n <a href=\"https://www.brandexperts.ae/warranty\">Click here</a><br />\r\nThe Events Team</span></span></p>','2025-03-21 10:56:37.739577',1,1,'New Event Announcement!',NULL,0,0),(2,'EID Mubarak to Customers','<p><span style=\"font-size:11pt; font-variant:normal; white-space:pre-wrap\"><span style=\"font-family:Arial,sans-serif\"><span style=\"color:#000000\"><strong><span style=\"font-style:normal\"><span style=\"text-decoration:none\">Dear [name]</span></span></strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt; font-variant:normal; white-space:pre-wrap\"><span style=\"font-family:Arial,sans-serif\"><span style=\"color:#000000\"><span style=\"font-weight:400\"><span style=\"font-style:normal\"><span style=\"text-decoration:none\">As the blessed days of Ramadan come to a close, we extend our warmest wishes of </span></span></span></span></span></span><span style=\"font-size:11pt; font-variant:normal; white-space:pre-wrap\"><span style=\"font-family:Arial,sans-serif\"><span style=\"color:#000000\"><strong><span style=\"font-style:normal\"><span style=\"text-decoration:none\">Eid Mubarak</span></span></strong></span></span></span><span style=\"font-size:11pt; font-variant:normal; white-space:pre-wrap\"><span style=\"font-family:Arial,sans-serif\"><span style=\"color:#000000\"><span style=\"font-weight:400\"><span style=\"font-style:normal\"><span style=\"text-decoration:none\"> to you and your team. May this season bring renewed joy, growth, and prosperity to your business and beyond.</span></span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt; font-variant:normal; white-space:pre-wrap\"><span style=\"font-family:Arial,sans-serif\"><span style=\"color:#000000\"><span style=\"font-weight:400\"><span style=\"font-style:normal\"><span style=\"text-decoration:none\">As we wrap up the Eid holidays, our hearts are full of gratitude and excitement. Whether you’ve been with us for years or are meeting us for the first time, this message is for you. At BrandExperts, we believe signages are more than just visuals—it’s a voice, a first “impression, a promise made visible.</span></span></span></span></span></span></p>\r\n\r\n<hr />\r\n<h3><span style=\"font-size:13pt; font-variant:normal; white-space:pre-wrap\"><span style=\"font-family:Arial,sans-serif\"><span style=\"color:#000000\"><strong><span style=\"font-style:normal\"><span style=\"text-decoration:none\">A Decade of Growth – Thanks to You!</span></span></strong></span></span></span></h3>\r\n\r\n<p><span style=\"font-size:11pt; font-variant:normal; white-space:pre-wrap\"><span style=\"font-family:Arial,sans-serif\"><span style=\"color:#000000\"><span style=\"font-weight:400\"><span style=\"font-style:normal\"><span style=\"text-decoration:none\">Since setting foot in the UAE in </span></span></span></span></span></span><span style=\"font-size:11pt; font-variant:normal; white